import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function WikiDetalle() {
  const { nombre } = useParams(); // Obtener el nombre de la URL
  const [wiki, setWiki] = useState(null); // Estado para almacenar los detalles de la wiki
  const [listaArticulos, setlistaArticulos] = useState([]);
  const [listaBusqueda, setListaBusqueda] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState(""); // Término con debounce.


   // Petición fetch inicial
   useEffect(() => {
    fetchWikiDatos();
    fetchArticulosWiki();
  }, [nombre]);

  useEffect(() => {
    // Configura un debounce: espera 300ms antes de actualizar el término de búsqueda.
    const handler = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300);

    return () => {
      clearTimeout(handler); // Limpia el temporizador si el usuario sigue escribiendo.
    };
  }, [searchTerm]);

  useEffect(() => {
    if (debouncedSearchTerm.trim()) {
      fetchBusqueda(debouncedSearchTerm);
    } else {
      setListaBusqueda([]); // Limpia los resultados si no hay término de búsqueda.
    }
  }, [debouncedSearchTerm]);



  const fetchWikiDatos = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:8001/api/v1/wikis/${nombre}`);
      const data = await response.json();
      setWiki(data); // Carga las wikis destacadas.
    } catch (error) {
      console.error("Error al obtener los detalles de la wiki:", error);
    }
  };

  const fetchArticulosWiki = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:8002/api/v1/wikis/${nombre}/articulos`);
      const data = await response.json();
      if (Array.isArray(data)) {
        setlistaArticulos(data); // Carga las wikis destacadas.
      } else {
        console.error("Error: La respuesta no es una lista.");
      }
    } catch (error) {
      console.error("Error al obtener la lista de articulos:", error);
    }
  };

  const fetchBusqueda = async (term) => {
    try {
      const response = await fetch(`http://127.0.0.1:8002/api/v1/wikis/${nombre}/articulos?term=${term}`);
      const data = await response.json();
      if (Array.isArray(data)) {
        setListaBusqueda(data); // Actualiza los resultados de búsqueda.
      } else {
        console.error("Error: La respuesta no es una lista.");
      }
    } catch (error) {
      console.error("Error al buscar articulos:", error);
    }
  };





  return (
    <div>
      {wiki ? (
        <>
          <h1>{wiki.nombre}</h1>
          <p>{wiki.descripcion}</p>
          <div className="input">
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Buscar Articulos..."
            />
          </div>

          {listaArticulos.length > 0 ? (
            <ul>
              {listaArticulos.map((listaArticulos, index) => (
                //<li key={index}>{listaArticulos.titulo}</li> // Asumiendo que cada wiki tiene un campo `nombre`
                <li><a href={`http://localhost:3000/wikis/${nombre}/${listaArticulos.titulo}`}>{listaArticulos.titulo}</a></li>
              ))}
            </ul>
          ) : (
            <p>No hay articulos disponibles</p>
          )}

        </>
      ) : (
        <p>Cargando detalles de la wiki...</p>
      )}
    </div>
  );
}

export default WikiDetalle;
