import logo from './logo.svg';
import './App.css';

import React, { useEffect, useState } from "react";

function App() {


   const [message, setMessage] = useState("");

  // Hacer la petición fetch cuando el componente se monta
  useEffect(() => {
    fetch("http://127.0.0.1:8001/api/v1/wikis") // Asegúrate de que la URL corresponde con tu backend
      .then((response) => response.json())
      .then((data) => {
        if (Array.isArray(data)) {
          // Si es una lista, puedes acceder al primer elemento o manejar la lista entera
          setMessage(data[0]?.nombre || "No hay wikis disponibles");
        } else {
          // Si devuelve un objeto JSON con un campo `message`
          setMessage(data.message);
        }
      })
      .catch((error) => console.error("Error al obtener el mensaje:", error));
  }, []);

  // Renderizar el mensaje en el HTML
  return (
    <div>
      <h1>Mensaje desde el backend:</h1>
      <p>{message}</p>
    </div>
    );
}

export default App;
