import logo from './logo.svg';
import './App.css';

import React, { useEffect, useState } from "react";

function App() {


   const [message, setMessage] = useState("");

  // Hacer la petición fetch cuando el componente se monta
  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/v1/wikis") // URL de la ruta en el backend FastAPI
      .then((response) => response.json()) // Convertir la respuesta a JSON
      .then((data) => {
        // Asignar el mensaje del JSON al estado
        setMessage(data.message);
      })
      .catch((error) => console.error("Error al obtener el mensaje:", error));
  }, []);

  // Renderizar el mensaje en el HTML
  return (
    <div>
      <h1>Mensaje desde el backend:</h1>
      <p>{message}</p>
    </div>
    );
}

export default App;
